<?phpnamespace Core\Controllers;class Controller{    public function render($view, array $params)    {        $file = ROOT_DIR.'/public/templates/'.$view.'.tmpl.php';        if (file_exists($file)) {            extract($params);            ob_clean();            ob_start();            include($file);            $output = ob_get_clean();            return $output;            } else {                return 'The file \'' . $file . '\' doesn\'t exist!!!';            }    }}